/**
    This updates the database with new settings for the user in req.user().
    @param {Object} req This is the Express req object
    @param {Object} res This is the Express res object
*/
export function newSettings(req, res){

    var color = req.body.color;
    var user_id = req.user.id;
    var newUser = req.body.username;
    var newEmail = req.body.email;
    var newPassword = req.body.password1;
    var verifyPassword = req.body.password2;
    var hash = bcrypt.hashSync(newPassword, bcrypt.genSaltSync(8), null);
    var values = {};
    if(color !== ''){
        values.color = color;
    }
    if(newUser !== ''){
        values.username = newUser;
    }
    if(newEmail !== ''){
        values.email = newEmail;
    }
    if(newPassword !== '' && hash !== null){
        values.password = hash;
    }

    sequelize.sync().then(function(){
        users.update(values, { where: { id: user_id } }).then(function(result){
            if(result){
                res.json({success: true});
            }
        });
    });
}

export function loadSettings(req, res){
    if(req.user){
        sequelize.sync().then(function(){
            users.findOne( { where: { id: req.user.id} }).then(function(result){
                 res.send(result);
            });
        });
    }
}

// Import model
import {sequelize, users } from '../models/index.js';
import bcrypt from 'bcryptjs';
/**
    Creates a new user with the form included in the req object and inserts into the database
    @param {Object} req This is the Express req object
    @param {Object} res This is the Express res object
*/
export function newUser(req, res){

    var username = req.body.username;
    var password = req.body.password;
    var email = req.body.email;
    var hash = bcrypt.hashSync(password, bcrypt.genSaltSync(8), null);
    var options = {
        where: {
            $or: [{username: username}, {email: email}]
        },
        defaults: {
            username: username,
            password: hash,
            email: email,
            color: "#705e8b",
        }
    };
    var newRecord = false;
    sequelize.sync().then(function(){
        users.findOrCreate(options).then(function(result){
            newRecord = result[1];
            if (!newRecord){
                var response = {};
                if (username == result[0].dataValues.username){
                    response = {
                        newUser: false,
                        existingField: 'username'
                    };
                }
                else{
                    response = {
                        newUser: false,
                        existingField: 'email'
                    }
                }
                res.send(response);
            }
            else{
                var response = {
                    newUser: true,
                };
                res.send(response);
            }
        });
    });
}

